-- Sally Database Dump Version 0.6
-- Prefix sly_

CREATE TABLE sly_article (id INTEGER NOT NULL, clang INTEGER NOT NULL, re_id INTEGER NOT NULL, name VARCHAR(255) NOT NULL, catname VARCHAR(255) NOT NULL, catprior INTEGER NOT NULL, attributes TEXT NOT NULL, startpage BOOLEAN NOT NULL, prior INTEGER NOT NULL, path VARCHAR(255) NOT NULL, status INTEGER NOT NULL, type VARCHAR(64) NOT NULL, createdate INTEGER NOT NULL, updatedate INTEGER NOT NULL, createuser VARCHAR(255) NOT NULL, updateuser VARCHAR(255) NOT NULL, revision INTEGER DEFAULT 0 NOT NULL, PRIMARY KEY("id", "clang"));
CREATE TABLE sly_article_slice (id INTEGER NOT NULL, clang INTEGER NOT NULL, slot VARCHAR(64) NOT NULL, prior INTEGER NOT NULL, slice_id INTEGER DEFAULT 0 NOT NULL, article_id INTEGER NOT NULL, createdate INTEGER NOT NULL, updatedate INTEGER NOT NULL, createuser VARCHAR(255) NOT NULL, updateuser VARCHAR(255) NOT NULL, revision INTEGER DEFAULT 0 NOT NULL, PRIMARY KEY("id"));
CREATE INDEX find_article ON sly_article_slice (article_id, clang);
CREATE TABLE sly_clang (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, name VARCHAR(255) NOT NULL, locale VARCHAR(5) NOT NULL, revision INTEGER DEFAULT 0 NOT NULL);
CREATE TABLE sly_file (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, re_file_id INTEGER NOT NULL, category_id INTEGER NOT NULL, attributes TEXT NOT NULL, filetype VARCHAR(255) NOT NULL, filename VARCHAR(255) NOT NULL, originalname VARCHAR(255) NOT NULL, filesize VARCHAR(255) NOT NULL, width INTEGER NOT NULL, height INTEGER NOT NULL, title VARCHAR(255) NOT NULL, createdate INTEGER NOT NULL, updatedate INTEGER NOT NULL, createuser VARCHAR(255) NOT NULL, updateuser VARCHAR(255) NOT NULL, revision INTEGER DEFAULT 0 NOT NULL);
CREATE INDEX filename ON sly_file (filename);
CREATE TABLE sly_file_category (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, name VARCHAR(255) NOT NULL, re_id INTEGER NOT NULL, path VARCHAR(255) NOT NULL, attributes TEXT NOT NULL, createdate INTEGER NOT NULL, updatedate INTEGER NOT NULL, createuser VARCHAR(255) NOT NULL, updateuser VARCHAR(255) NOT NULL, revision INTEGER DEFAULT 0 NOT NULL);
CREATE TABLE sly_user (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, name VARCHAR(255) NOT NULL, description VARCHAR(255) NOT NULL, login VARCHAR(50) NOT NULL, psw CHAR(40), status BOOLEAN NOT NULL, rights TEXT NOT NULL, lasttrydate INTEGER DEFAULT 0 NOT NULL, timezone VARCHAR(64) NULL, createdate INTEGER NOT NULL, updatedate INTEGER NOT NULL, createuser VARCHAR(255) NOT NULL, updateuser VARCHAR(255) NOT NULL, revision INTEGER DEFAULT 0 NOT NULL);
CREATE TABLE sly_slice (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, module VARCHAR(64) NOT NULL);
CREATE TABLE sly_slice_value (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, slice_id INTEGER NOT NULL, type VARCHAR(50) NOT NULL, finder VARCHAR(50) NOT NULL, value TEXT NOT NULL);
CREATE INDEX slice_id ON sly_slice_value (slice_id);
CREATE TABLE sly_registry (name VARCHAR(255) NOT NULL, value BLOB NOT NULL, PRIMARY KEY("name"));

